            <MenuIcon />
          </IconButton>
          <h1>Change Text</h1>
        </Toolbar>
      </AppBar>
      <div>
        <TextField name="example" label="text" value={text} onChange={handleChange} />
        <Button onClick={handleClick}>Clear</Button>
      </div>

      <br />

      <div>text: {text}</div>

      <div style={{ marginTop: 40 }}>
        <Link href="/example">back</Link>
      </div>
    </div>
  );
};

export default Example;
